import React,{useState} from "react";


export default function Textform(props) {

  const handleUpClick=()=>{
    // console.log("UPPERCASE was clicked")
    let newText=text.toUpperCase();
    setText(newText)
    props.showAlert("Converted to Upper Case!", "success");
  };
  const handleCopy = () => {
    let text = document.getElementById("myBox");
    text.select();
    navigator.clipboard.writeText(text.value);
    props.showAlert("Clipboard was Copied!", "success")
    }
    const handleExtraSpaces = () => {
      let newText = text.split(/[ ]+/);
      setTex(newtext.join(" "))
      }

  const handleLoClick=()=>{
    // console.log("lowercase was clicked")
    let newText=text.toLowerCase();
    setText(newText)
    props.showAlert("Converted to Lower Case!", "success");
  };
  const handleClearClick=()=>{
    // console.log("Clear text was clicked")
    let newText=''
    setText(newText)
    props.showAlert("Cleared the box!", "success");
  }

  const handleOnChange=(event)=>{
    // console.log("On Change")
    setText(event.target.value);
  }



  const [text, setText] = useState("");
  // setText("new Text");   //correct Waw to Set A new Text

  return (
    <>
    <div className="container my-3 " style={{color:props.mode ==='dark'?'white':'#042743'}}>
     <h1>{props.heading} </h1>
       <div className="mb-3">

        <textarea className="form-control" value={text} placeholder="Enter Your Text Here" id="myBox" style={{backgroundColor:props.mode ==='light'?'white':'grey' ,color:props.mode ==='dark'?'white':'#042743'}} onChange={handleOnChange} rows="8"></textarea>

        <button className="btn btn-dark mx-1 my-2" onClick={handleUpClick} >Convert to Upper Case</button>
        <button className="btn btn-light mx-1 my-2" onClick={handleLoClick} >Convert to Lower Case</button>
        <button className="btn btn-dark mx-1 my-2" onClick={handleClearClick} >  Clear Text  </button>
        <button className= "btn btn-light mx-1" onClick={handleCopy}> Copy Text</button>


      </div>
    </div>
       <div className="container my-3" style={{color:props.mode ==='dark'?'white':'#042743'}}>
        <h2>Your text summary</h2>
        <p>{text.split(" ").length} words and {text.length} characters</p>
        <p>{0.008 * text.split(" ").length} minutes to read</p>

        <h2>Preview</h2>
        <p>{text.length>0?text:"Enter Something Above To Preview"}</p>
       </div>


  </>
  )
}
